create database bd_maromba;
use bd_maromba;

create table Produtos(
    id int unsigned primary key not null auto_increment,
    nome varchar(200) not null,
    descricao longtext not null,
    preco decimal(9,2) not null,
    quantidade int not null,
    categoria varchar(100) not null,
    imagem varchar(3000) not null
);

create table Clientes(
    id int unsigned primary key not null auto_increment,
    nome varchar(30) not null,
    sobrenome varchar(30) not null,
    endereco varchar(60),
    telefone varchar(20),
    RG varchar(20),
    CPF varchar(20),
    email varchar(50),
    genero enum('m','f') not null,
    birthdate date
);

create table Pedidos(
numero int unsigned primary key not null auto_increment,
pcodigo int unsigned,
dtElaboracao date not null,
foreign key (numero) references Clientes(id),
foreign key (pcodigo) references Produtos(id)
);


create table Comentarios(
    numero int unsigned primary key not null auto_increment,
    idCliente int unsigned not null,
    pcodigo int unsigned not null,
    texto mediumtext not null,
    foreign key (idCliente) references Clientes(id),
    foreign key (pcodigo) references Produtos(id)
);

